CREATE TABLE product_categories
(
  id          int(10) unsigned NOT NULL AUTO_INCREMENT,
  name        varchar(50)   NOT NULL,
  description varchar(2000) NOT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
INSERT INTO product_categories
VALUES (1, 'Кидсы',
        '<br>\nУдобные и модные детские Кидсы <br><br>\n\nЛегкие, красивые, яркие <br><br>\n\nОтличное качество<br><br>\n\nВаш малыш будет в восторге <br><br>\n\nМатериал: полимер на основе мягкого  пластика <br><br>\n\nСтрана производитель: Гонконг <br><br>\n\nВ наличии все размеры: <br><br>\n\n24 размер, 2,5 года, стопа 14,6 см<br>\n25 размер, 2,5 — 3 года, стопа 15,3 см<br>\n26 размер, 3 — 4 года, стопа 16,5 см <br>\n27 размер, 4 — 4,5 года, стопа 17,3 см <br>\n28 размер, 4,5 — 5 лет, стопа 17,6 см <br>\n29 размер, 5-6 лет, стопа 18,5 см<br><br>\n\nДоставка: <br><br>\n\n— самовывоз по адресу г.Киев, пр. Бажана 10 (метро Осокорки) — бесплатно <br>\n— доставка курьером до любой станции метро (г. Киев) — 20 грн <br>\n— доставка курьером по указанному адресу (г.Киев) — 30 грн <br>\n— доставка Укрпочтой или Новой Почтой в любой город Украины — 25 грн <br><br>\n\nОплата: <br><br>\n— наличными курьеру при получении товара <br>\n— предоплата на карточку Приватбанка <br>'),
       (2, 'Пижамы',
        '<br> Детская пижама с длинным рукавом + длинные брюки (комплект) <br><br>\n\nЛегкая, теплая, уютная, красивая и яркая<br><br>\n\nОтличное качество<br><br>\n\nВаш малыш будет в восторге<br><br>\n\nМатериал: 100% хлопок <br><br>\n\nСтрана производитель: Гонконг <br><br>\n\nВ наличии все размеры: <br><br>\n\nРазмер 2Т (2 года) — Рост- 90 см<br>\nРазмер 3Т (3 года) — Рост — 95 см<br>\nРазмер 4т (4 года) — Рост — 100 см<br>\nРазмер 5т (5 лет) — Рост — 110 см<br>\nРазмер 6т (6 лет) — Рост — 120 см<br>\nРазмер-7т (7 лет) — Рост — 130 см<br><br>\n\n<div class = \"hidden-xs\">\n<br>\n<img src=\"/resources/img/sizes/ironman.png\">\n<img src=\"/resources/img/sizes/superman.png\">\n<img src=\"/resources/img/sizes/spiderman.png\">\n<img src=\"/resources/img/sizes/hellokitty.png\">\n<br>\n<br>\n</div>\nДоставка:<br><br>\n\n— самовывоз по адресу г.Киев, пр. Бажана 10 (метро Осокорки) — бесплатно <br>\n— доставка курьером до любой станции метро (г. Киев) — 20 грн <br>\n— доставка курьером по указанному адресу (г.Киев) — 30 грн<br>\n— доставка Укрпочтой или Новой Почтой в любой город Украины — 25 грн<br><br>\n\nОплата:<br><br>\n— наличными курьеру при получении товара<br>\n— предоплата на карточку Приватбанка<br>'),
       (3, 'Трусики',
        '<br>Детские трусики-боксеры для мальчиков<br><br>\n\nВ серии представлены трусы с различными принтами: Angry birds, Cars, Spiderman, SpongeBob, Mickey Mouse, Doreamon, Thomas<br><br>\n\nЗадняя сторона трусиков однотонная<br><br>\n\nВаш малыш будет в восторге<br><br>\n\nОтличное качество<br><br>\n\nМатериал: 100% хлопок<br><br>\n\nСтрана производитель: Гонконг<br><br>\n\nВ наличии размеры:<br><br>\n\nРазмер S, рост 100-110 см, талия 22 см * 2, 4-6 лет<br>\nРазмер M, рост 110-120 см, талия 24 см * 2, 7-8 лет<br>\nРазмер L, рост 120-130 см, талия 26 см * 2, 9-10 лет<br><br>\n\nДоставка:<br><br>\n\n— самовывоз по адресу г.Киев, пр. Бажана 10 (метро Осокорки) — бесплатно<br>\n— доставка курьером до любой станции метро (г. Киев) — 20 грн<br>\n— доставка курьером по указанному адресу (г.Киев) — 30 грн<br>\n— доставка Укрпочтой или Новой Почтой в любой город Украины — 25 грн<br><br>\n\nОплата:<br><br>\n— наличными курьеру при получении товара<br>\n— предоплата на карточку Приватбанка<br>');
